import React, { useEffect, useMemo, useState } from 'react'
import Cards from '../components/Cards'
import Footer from '../components/Footer'
import Header from '../components/Header'
import SideBar from '../components/SideBar'
import '../styles/Main.scss'

/**
* @author Decory Herbert
* @function Home
**/

const Home = (props) => {
    const data_from_db = []
    const data_list = []
    const [dataCheck, setDataCheck] = useState([])
    const [csv, setCsv] = useState([])
    const [length, setLength] = useState(0)

    var off_obj = {}
    var i

    useEffect(()=>{
        fetch('https://bottlecapdev.pythonanywhere.com/return_bloodorange_status',{
            method: 'GET',
            headers: {'Content-Type': 'application/json',}
        }).then(response => response.json())
        .then((resp)=>{
            console.log(resp)
            for(i=0; i<resp.data.length; i++){
                console.log(resp.data[i].dob)
                data_from_db.push({'name': resp.data[i].fullname, 'province': resp.data[i].province, 'email': resp.data[i].email[i], 'points': resp.data[i].points})
                data_list.push({'name': resp.data[i].fullname, 'address': resp.data[i].address, 'province': resp.data[i].province})
                setDataCheck(data_from_db)
                setCsv(data_list)
            }
            setLength(resp.data.length)
        })

    },[])




  return(
    <div id='dash_Home'>
        <SideBar />
        <div id='content_holder'>
            <Header />
            <div id='content_wrapper'>
            <Cards total={length}/>


            <div id='table_content'>
            <table> 
                <tbody>
                <tr>
                    <th>Name</th>
                    <th>address</th>
                    <th>province</th>
                    <th>email</th>
                    <th>Tel</th>
                    <th>Points</th>
                    <th>Prize</th>
                    <th>Ballots</th>
                </tr>
                </tbody>
                <tbody>
                {
                         dataCheck.map((data, index) =>{
                            return(
                                <tr>
                                    <td key={index}>{data.name}</td>
                                    <td key={index}>{data.address}</td>
                                    <td key={index}>{data.province}</td>
                                    <td key={index}>{data.email}</td>
                                    <td key={index}>{data.tel}</td>
                                    <td key={index}>{data.points}</td>
                                    <td key={index}>{data.prize}</td>
                                    <td key={index}>{data.ballots}</td>

                                </tr>

                            )
                        })
                }

                </tbody>


            </table>
            </div>
            <Footer data={csv}/>

            </div>

        </div>

    </div>
   )

 }

export default Home


